const axiosW = require('./../utils/axios-wrapper');

function findWorkstationGroup(event, workstationGroupnId) {
    return axiosW.platformGet(event, `/hcm/payroll/entities/workstationgroup/${workstationGroupnId}`);
}

function findWorkstationGroupProfile(event, workstationGroupProfileId) {
    return axiosW.platformGet(event, `/hcm/general_register/entities/workstationGroupProfile/${workstationGroupProfileId}`);
}

module.exports = {
    findWorkstationGroup,
    findWorkstationGroupProfile
};
